#This function takes care of writing to a log file and also to stdin when requested
def printLog(logFileName, string, stdin = True):
    original = sys.stdout
    sys.stdout = open(logFileName, 'a+')
    print(string)
    sys.stdout.flush()
    sys.stdout.close()
    sys.stdout = original
    if (stdin):
        print(string)



