#Creates a mapping between our genes to all of the species specific genes found in kegg, providing that the name matches exactly with one of the synonyms
#TODO: create different ways of writing the genes, for example replacing "-" with " " etc
#TODO: DIFFERENT WAYS OF WRITING:        geneKeySearchHelper = {geneKey, re.sub(r"\((\d)\)", "\g<1>",geneKey)}
def createFileHashOfOurGenesToKEGGSpecificGenesMatchingTheWholeNameOfASynonym(filenameGenes, filenameToSave):
    logFileName = "LogCreateFileHashOfOurGenesToKEGGSpecificGenesMatchingTheWholeNameOfASynonym.log"
    printLog(logFileName,"createFileHashOfOurGenesToKEGGSpecificGenesMatchingTheWholeNameOfASynonym")
    OurGenesToKEGGSpecific = CaseInsensitiveDict()
    for line in open(filenameGenes):
        gene = line.replace("\n", "").replace("\r", "")
        OurGenesToKEGGSpecific[gene] = set()
        geneInAllOrganisms = k.find("genes", gene)
        printLog(logFileName,"  Gene: " + gene)
        if (geneInAllOrganisms != 400):
            #For each gene of different organism
            for geneFound in geneInAllOrganisms.split("\n"):
                if(len(geneFound) > 1):
                    id,names = geneFound.split("\t")
                    #Make sure it matches exactly with at least one synonym
                    for synonym in (names.split(";")+names.split(",")):
                        if (synonym == gene):
                            OurGenesToKEGGSpecific[gene].add(id +"\t"+synonym)
                            printLog(logFileName,"      IDMatching Name: "+  id +"\t"+synonym)

    printLog(logFileName,"Saving Our Gene to KEGG Gene in  file")
    # save this list to a file to avoid calling keggs api
    with open(filenameToSave, 'w') as csv_file:
        writer = csv.writer(csv_file, delimiter='$')
        writer.writerow(["OurGeneName", "KEGGGeneName"])
        for gene in OurGenesToKEGGSpecific.keys():
            strOutput = '|'.join(list(OurGenesToKEGGSpecific[gene])).replace('"', '')
            writer.writerow([gene, strOutput])




#Outputs a file with a list of all possible reactions based on the capabilities of the enzymes present
#The file is encoded as a hashtable, that maps a enzyme to a list of possible reactions
#TODO: What does it mean for a reaction to have a (G)?: R06204(G). For the same reaction, what does compoind G10477(n) mean?
#TODO: What does it mean for a reaction to have (other) at the start of the name?: (other) R12291
#TODO: Ignoring enzymes without ALL_REAC even if they have a reaction described by words.
def createFileHashOfGenesToPossibleReactions(ourGenesToKEGGEnzymes,filename):
    logFileName = "createFileHashOfGenesToPossibleReactions.log"
    outputReactionHash = CaseInsensitiveDict()
    printLog(logFileName,"createFileHashOfGenesToPossibleReactions")
    #Iterate for all the enzymes
    for geneKey in ourGenesToKEGGEnzymes.keys():
        outputReactionHash[geneKey] = set()
        printLog(logFileName,"  Gene: "+ geneKey)
        for enzyme in ourGenesToKEGGEnzymes[geneKey]:
            if (len(enzyme)< 2):
                continue
            dict_enzyme = k.parse(k.get(enzyme))
            try:
                reactions = dict_enzyme["ALL_REAC"]
                printLog(logFileName,"      Enzyme: " + enzyme)
            except KeyError:
                printLog(logFileName,"      Enzyme: " + enzyme + " ALL_REAC NOT FOUND")
                continue
            #Get the reactions for this enzyme
            for reaction in reactions:
                if (reaction == "(other)" or len(reaction) < 2):
                    continue
                reaction = reaction.replace("(G)","")
                reactionEquation = re.sub(' \d ', ' ', k.parse(k.get(reaction))["EQUATION"])
                printLog(logFileName,"          Reaction: " + reaction+ ": " + reactionEquation)
                outputReactionHash[geneKey].add(reactionEquation)
    # Save the file with all the reactions for every enzyme
    printLog(logFileName,"  Saving Reactions file: " + filename)
    with open(filename, 'w') as csv_file:
        writer = csv.writer(csv_file, delimiter='$')
        writer.writerow(["Enzyme", "Reactions"])
        for reaction in outputReactionHash.keys():
            strOutput = '|'.join(list(outputReactionHash[reaction])).replace('"', '')
            writer.writerow([reaction, strOutput])



# create mapping from our gene to kegg enzyme. uses the our gene to kegg gene mapping, access the kegg gene and get to the enzyme
def createFileHashOfOurGenesToKEGGEnzymes(ourGenesToKEGGSpecific,filename):
    logFileName = "createFileHashOfOurGenesToKEGGEnzymes.log"
    genesToEnzymesHash = CaseInsensitiveDict()
    printLog(logFileName,"createFileHashOfOurGenesToKEGGEnzymes")
    #Iterate for all the genes
    for geneKey in ourGenesToKEGGSpecific.keys():
        genesToEnzymesHash[geneKey] = set()
        printLog(logFileName,"  Gene: "+ geneKey)
        #Iterate for all the kegg specific versions of the gene
        for keggGene in ourGenesToKEGGSpecific[geneKey]:
            printLog(logFileName,"      KEGGGene: " + keggGene)
            result = k.parse(k.get(keggGene))
            if (result == 400):
                continue
            geneData = str(result.get("ORTHOLOGY",""))
            if(geneData == ""):
                continue
            startEnzyme = geneData.find("[EC:")
            endEnzyme = geneData.find("]",startEnzyme)
            enzymes = geneData[(startEnzyme+4):(endEnzyme)]
            for enzyme in enzymes.split(" "):
                printLog(logFileName,"          Enzyme: " + enzyme)
                genesToEnzymesHash[geneKey].add(enzyme)

    # do some post processing to remove badly formatted enzymes
    pattern = re.compile("^\d+\.\d+\.\d+\.\d+$")
    for key in genesToEnzymesHash.keys():
        properFormatList = list()
        for value in genesToEnzymesHash[key]:
            if (pattern.match(value) != None):
                properFormatList.append(value)
        genesToEnzymesHash[key] = properFormatList

    # Save the file
    printLog(logFileName,"  Saving mapping from Our Gene to KEGG Enzyme: " + filename)
    with open(filename, 'w') as csv_file:
        writer = csv.writer(csv_file, delimiter='$')
        writer.writerow(["OurGene", "KEGGEnzymes"])
        for gene in genesToEnzymesHash.keys():
            strOutput = '|'.join(list(genesToEnzymesHash[gene])).replace('"', '')
            writer.writerow([gene, strOutput])



#Outputs a file of the set of enzymes present in all strains of this species
#This is encode as a hash that maps a taxa to a set of enzymes
def createFileHashOfTaxaToGenes(taxaToStrains, filename):
    logFileName = "LogCreateFileHashOfTaxaToEnzymes.log"
    printLog(logFileName,"Getting all the enzymes of each organism...")
    # Make a list with all enzymes for the species (union of all enzymes of all strains)
    organismEnzymes = CaseInsensitiveDict()
    for organism in taxaToStrains.keys():
        organismEnzymes[organism] = set()
        printLog(logFileName,"  " + organism + ". strains:")
        for strain in taxaToStrains[organism]:
            if (strain == None):
                continue
            enzymes = k.list(strain).split("\n")
            printLog(logFileName,"      " + strain+ ". " + str(len(enzymes)) +  " enzymes found.")
            for enzyme in enzymes:
                if (enzyme != ""): organismEnzymes[organism].add(enzyme.split("\t")[1])

    printLog(logFileName,"Saving interactions file: "+ filename)
    # Save the file with the enzymes of the organism
    with open(filename, 'w') as csv_file:
        writer = csv.writer(csv_file, delimiter='$')
        writer.writerow(["SpeciesName", "EnzymeNames"])
        for organism in organismEnzymes.keys():
            strOutput = '|'.join(list(organismEnzymes[organism])).replace('"', '')
            writer.writerow([organism, strOutput])



