#Returns a map from every taxa to the metabolites it is capable of producing
#check the substrate to validate the product, but also look if we have a match of the product into the hmdb database
def createFileHashOfTaxaToMetaboliteInterations(genesToReactions,taxaToGenes, metabolitesPresent, genesExpressed, HMDBToKEGG,filename):
    logFileName = "LogCreateFileHashOfTaxaToMetaboliteInterations.log"
    printLog(logFileName,"Getting all the metabolites a taxa is capable of producing...")

    # Reverse HMDBToKEGG
    KEGGToHMDB = {list(HMDBToKEGG[k])[0]:k for k in HMDBToKEGG.keys()}

    # We first filter the dictionaries for the genes that are actually expressed
    genesToReactions = {k: genesToReactions[k] for k in genesToReactions.keys() & genesExpressed} #Filter the keys
    taxaToGenes = {k:(taxaToGenes[k] & genesExpressed) for k in taxaToGenes.keys()} # Filter the values

    # Add water to the metabolites present, we assume that is always avalable
    metabolitesPresent.add("C00001")

    taxaToMetabolitesFileHash = CaseInsensitiveDict()
    #Get all taxa
    for taxa in taxaToGenes.keys():
        taxaToMetabolitesFileHash[taxa] = set()
        printLog(logFileName,"  Taxa: "+ taxa)
        #Get all enzymes for that taxa
        for gene in taxaToGenes.get(taxa):
            printLog(logFileName,"      Gene: " + gene)
            # Get the reactions that this gene performs
            for reaction in genesToReactions.get(gene,""):
                if (reaction != "" and reaction != None and reaction != ["\""] and reaction != [""] ):
                    printLog(logFileName, "         Reaction: " + reaction)
                    # reactions= ["C00354 + C00001 <=> C00085 + C00009"]
                    substrate, product = reaction.split(" <=> ")
                    substrate = set(substrate.split(" + "))
                    product = set(product.split(" + "))

                    # If every compound is present in one side of the reaction, then it can produce the compounds on the other side
                    if (substrate & metabolitesPresent == substrate):
                        # If we have information about the product
                        printLog(logFileName, "             Present: " + str(substrate ))
                        for metabolite in product:
                            if (KEGGToHMDB.get(metabolite,"") != ""):
                                print("             Adding: " + str(metabolite))
                                taxaToMetabolitesFileHash[taxa].add(metabolite)

                    if (product & metabolitesPresent == product):
                        # If we have information about the product
                        printLog(logFileName, "             Present: " + str(product))
                        for metabolite in substrate:
                            # If we have information about the substrate
                            if (KEGGToHMDB.get(metabolite, "") != ""):
                                print("             Adding: " + str(metabolite))
                                taxaToMetabolitesFileHash[taxa].add(metabolite)

    # Save the file with all the relations from taxa to metabolite
    printLog(logFileName,"  Saving Taxa to Metabolites file: " + filename)
    with open(filename, 'w') as csv_file:
        writer = csv.writer(csv_file, delimiter='$')
        writer.writerow(["Taxa", "KEGGCompounds", "HMDBMetabolites"])
        for taxa in taxaToMetabolitesFileHash.keys():
            strOutput = '|'.join(list(taxaToMetabolitesFileHash[taxa])).replace('"', '')
            strOutputHMDB = '|'.join([KEGGToHMDB[x].replace('"', '') for x in list(taxaToMetabolitesFileHash[taxa])])
            writer.writerow([taxa, strOutput,strOutputHMDB])
    return False




#Load the specified hash table from a csv file
def loadHashTable(filename):
    printLog(logFileName,"Loading "+filename+"...")
    try:
        file = open(filename, 'r')
    except OSError:
        printLog(logFileName,"  ", filename + " does not exist")
        return None
    hash = CaseInsensitiveDict()
    for i, line in enumerate(file):
        if (i == 0 or line == "\n" or line == "\r"):
            continue
        key, values = line.replace("\n","").replace("\r","").split("$")
        if (values != ""):
            hash[key] = set(values.split("|"))
        # hash[key] = list(values.split("|"))
        hash[key] = set(values.split("|"))
        # printLog("a.txt",  str(key) + "$" +  str(values))
    return hash





